function getKeySite(keyStroke) {
      isNetscape=(document.layers);
      eventChooser = (isNetscape) ? keyStroke.which : event.keyCode;
      which = String.fromCharCode(eventChooser).toLowerCase();
      keyBuffer = which;
      if(eventChooser == 13){ 
                              bloqueia();
