public String getCli_rzao() {
com.egen.util.jdbc.JdbcUtil j = null;
try {
  if (cli_cdgo !=null && cli_cdgo.length()>0) {
    j = new com.egen.util.jdbc.JdbcUtil();
    Cli db_object = new Cli();
    String[][] select = {
      {"cli_rzao",null}
      };
    Object[][] where = {
      {"cli_cdgo","=",com.egen.util.text.FormatNumber.toInt(cli_cdgo)},
      };
    String[] group = null;
    String[] having = null;
    String[] order = null;
    java.util.Vector results = j.select(db_object, select, where, group, having, order);
    if (results!=null && results.size()>0) {
      db_object = (Cli)results.elementAt(0);
      cli_rzao = (db_object.getCli_rzao()!=null)?db_object.getCli_rzao()+"":"";
    } else {
      cli_rzao = "";
    }
  }
} catch (Exception e) {
      cli_rzao = "";
} finally {
  if (j!=null) { j.close(); }
}
return cli_rzao;
}

