public String getLin_nome() {
com.egen.util.jdbc.JdbcUtil j = null;
try {
  if (lin_cdgo !=null && lin_cdgo.length()>0) {
    j = new com.egen.util.jdbc.JdbcUtil();
    Lin db_object = new Lin();
    String[][] select = {{"lin_nome",null}};
    Object[][] where = {
                          {"lin_cdgo","=",com.egen.util.text.FormatNumber.toInt(lin_cdgo)}
                       };
    String[] group = null;
    String[] having = null;
    String[] order = null;
    java.util.Vector results = j.select(db_object, select, where, group, having, order);
    if (results!=null && results.size()>0) {
      db_object = (Lin)results.elementAt(0);
      lin_nome = (db_object.getLin_nome()!=null)?db_object.getLin_nome()+"":"";
    } else {
      lin_nome = "";
    }
  }
} catch (Exception e) {
      lin_nome = "";
} finally {
  if (j!=null){
     j.close();
     j = null;
  }
}
return lin_nome;
}

