} public java.util.Vector getCodigo_motivo_trocaList() {
  if (codigo_motivo_trocaList == null || codigo_motivo_trocaList.size() == 0) {
    codigo_motivo_trocaList = new Vector(10, 10);
    codigo_motivo_trocaLabelList = new Vector(10, 10);
